-- Crear schema
CREATE SCHEMA IF NOT EXISTS schema_otg3;

-- ========================
-- regions
-- ========================
CREATE TABLE schema_otg3.regions (
    region_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 5)
        PRIMARY KEY,
    region_name VARCHAR(50) NOT NULL
) TABLESPACE datos;

-- ========================
-- countries
-- ========================
CREATE TABLE schema_otg3.countries (
    country_id CHAR(2) PRIMARY KEY,
    country_name VARCHAR(40) NOT NULL,
    region_id BIGINT,
    CONSTRAINT fk_countries_regions FOREIGN KEY(region_id)
        REFERENCES schema_otg3.regions(region_id)
        ON DELETE CASCADE
) TABLESPACE datos;

-- ========================
-- locations
-- ========================
CREATE TABLE schema_otg3.locations (
    location_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 24)
        PRIMARY KEY,
    address VARCHAR(255) NOT NULL,
    postal_code VARCHAR(20),
    city VARCHAR(50),
    state VARCHAR(50),
    country_id CHAR(2),
    CONSTRAINT fk_locations_countries FOREIGN KEY(country_id)
        REFERENCES schema_otg3.countries(country_id)
        ON DELETE CASCADE
) TABLESPACE datos;

-- ========================
-- warehouses
-- ========================
CREATE TABLE schema_otg3.warehouses (
    warehouse_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 10)
        PRIMARY KEY,
    warehouse_name VARCHAR(255),
    location_id BIGINT,
    CONSTRAINT fk_warehouses_locations FOREIGN KEY(location_id)
        REFERENCES schema_otg3.locations(location_id)
        ON DELETE CASCADE
) TABLESPACE datos;

-- ========================
-- employees
-- ========================
CREATE TABLE schema_otg3.employees (
    employee_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 108)
        PRIMARY KEY,
    first_name VARCHAR(255) NOT NULL,
    last_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
    phone VARCHAR(50) NOT NULL,
    hire_date DATE NOT NULL,
    manager_id BIGINT,
    job_title VARCHAR(255) NOT NULL,
    CONSTRAINT fk_employees_manager FOREIGN KEY(manager_id)
        REFERENCES schema_otg3.employees(employee_id)
        ON DELETE CASCADE
) TABLESPACE datos;

-- ========================
-- product_categories
-- ========================
CREATE TABLE schema_otg3.product_categories (
    category_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 6)
        PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
) TABLESPACE datos;

-- ========================
-- products
-- ========================
CREATE TABLE schema_otg3.products (
    product_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 289)
        PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    description VARCHAR(2000),
    standard_cost NUMERIC(9,2),
    list_price NUMERIC(9,2),
    category_id BIGINT NOT NULL,
    CONSTRAINT fk_products_categories FOREIGN KEY(category_id)
        REFERENCES schema_otg3.product_categories(category_id)
        ON DELETE CASCADE
) TABLESPACE datos;

-- ========================
-- customers
-- ========================
CREATE TABLE schema_otg3.customers (
    customer_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 320)
        PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    address VARCHAR(255),
    website VARCHAR(255),
    credit_limit NUMERIC(8,2)
) TABLESPACE datos;

-- ========================
-- contacts
-- ========================
CREATE TABLE schema_otg3.contacts (
    contact_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 320)
        PRIMARY KEY,
    first_name VARCHAR(255) NOT NULL,
    last_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
    phone VARCHAR(20),
    customer_id BIGINT,
    CONSTRAINT fk_contacts_customers FOREIGN KEY(customer_id)
        REFERENCES schema_otg3.customers(customer_id)
        ON DELETE CASCADE
) TABLESPACE datos;

-- ========================
-- orders
-- ========================
CREATE TABLE schema_otg3.orders (
    order_id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 106)
        PRIMARY KEY,
    customer_id BIGINT NOT NULL,
    status VARCHAR(20) NOT NULL,
    salesman_id BIGINT,
    order_date DATE NOT NULL,
    CONSTRAINT fk_orders_customers FOREIGN KEY(customer_id)
        REFERENCES schema_otg3.customers(customer_id)
        ON DELETE CASCADE,
    CONSTRAINT fk_orders_employees FOREIGN KEY(salesman_id)
        REFERENCES schema_otg3.employees(employee_id)
        ON DELETE SET NULL
) TABLESPACE datos;

-- ========================
-- order_items
-- ========================
CREATE TABLE schema_otg3.order_items (
    order_id BIGINT NOT NULL,
    item_id BIGINT NOT NULL,
    product_id BIGINT NOT NULL,
    quantity NUMERIC(8,2) NOT NULL,
    unit_price NUMERIC(8,2) NOT NULL,
    CONSTRAINT pk_order_items PRIMARY KEY(order_id, item_id),
    CONSTRAINT fk_order_items_products FOREIGN KEY(product_id)
        REFERENCES schema_otg3.products(product_id)
        ON DELETE CASCADE,
    CONSTRAINT fk_order_items_orders FOREIGN KEY(order_id)
        REFERENCES schema_otg3.orders(order_id)
        ON DELETE CASCADE
) TABLESPACE datos;

-- ========================
-- inventories
-- ========================
CREATE TABLE schema_otg3.inventories (
    product_id BIGINT NOT NULL,
    warehouse_id BIGINT NOT NULL,
    quantity BIGINT NOT NULL,
    CONSTRAINT pk_inventories PRIMARY KEY(product_id, warehouse_id),
    CONSTRAINT fk_inventories_products FOREIGN KEY(product_id)
        REFERENCES schema_otg3.products(product_id)
        ON DELETE CASCADE,
    CONSTRAINT fk_inventories_warehouses FOREIGN KEY(warehouse_id)
        REFERENCES schema_otg3.warehouses(warehouse_id)
        ON DELETE CASCADE
) TABLESPACE datos;

